import React from 'react'

const Categories = ({ filterItems, allCategories }) => {
  return (
    <div className="btn-container">
      <button
        className="filter-btn"
        onClick={() => {
          filterItems('all')
        }}
      >
        all
      </button>
      {allCategories.map((allCategory) => {
        return (
          <button
            className="filter-btn"
            onClick={() => {
              filterItems(allCategory)
            }}
          >
            {allCategory}
          </button>
        )
      })}
    </div>
  )
}

export default Categories
