    let amount = parseInt(count)

    // generate text based on the number inputted
    if (count <= 0) {
      amount = 1
    }
    if (count > 8) {
      amount = 8
    }
    console.log(setText(data.slice(0, amount)))
  }

  return (
    <section className="section-center">
      <h3>Tired of boring Lorem Ipsum?</h3>
