import React from 'react'

const Categories = ({ filterItems, allCategories }) => {
  return (
    <div className="btn-container">
      <button
        className="filter-btn"
        onClick={() => {
          filterItems('all')
        }}
      >
        all
      </button>

      <button
        className="filter-btn"
        onClick={() => {
          filterItems(allCategories)
        }}
      >
        {allCategories}
      </button>
    </div>
  )
}

export default Categories
